// Fichero src/components/App.js
import {useState} from 'react';
import '../styles/App.scss'
import adalabers from '../data/adalabers.json';

function App() {
  //-------VARIABLES DE ESTADO
const [data, setData] = useState(adalabers);
const [newAdalaber, setNewAdalaber] = useState({
  name:'',
  counselor: '',
  speciality:'',
 });


///-----

const renderAdalabers = data.map((adalaber) => {
return (
<tr className="adalaber__info">
      <td className="adalaber__name">{adalaber.name}</td>
      <td className="adalaber__tutor">{adalaber.counselor}</td>
      <td className="adalaber__specialty">{adalaber.speciality}</td>
</tr>

);
});


return (
<div className="page" >
<header className="header">
  <h1>Adalabers</h1>
</header>
<main>
<table className="table">
<thead><tr>
    <th>Nombre</th>
    <th>Tutora</th>
    <th>Especialidad</th>
</tr></thead>
<tbody>
{renderAdalabers}
</tbody>
</table> 
<form className="new-contact__form">
          <h2 className="new-contact__title">Añade un nuevo contacto</h2>
          <input
            className="new-contact__input"
            type="text"
            name="name"
            placeholder="Nombre"

          />
          <input
            className="new-contact__input"
            type="counselor"
            name="counselor"
            placeholder="Tutor"

          />
          <input
            className="new-contact__input"
            type="speciality"
            name="speciality"
            placeholder="Especialidad"
          />

        </form>
        <button className="new-contact__btn" type="submit">Añadir una nueva Adalaber</button>
</main>
</div>
  );
}
export default App;
